import React, { Component } from 'react';
class TodoForm  extends Component {
    constructor(props)
    {
        super(props);
        this.state={
            todotext:""
        }
    }
    addTodo=(e)=>
    {
        e.preventDefault();
        this.props.addTodo();
    }
    render() { 
        return ( 

            <div>
                <form onSubmit={this.addTodo}>
                <input type='text' value={this.state.todotext} onChange={(e)=>this.setState({todotext:e.target.value})} placeholder="Add your todo here"/>
                </form>
            </div>
         );
    }
}
 
export default TodoForm;
