import React,{Component} from 'react'
import './App.css'
import Todo from './components/Todo.js'
export default class App extends Component()
{
    constructor(props)
    {
        super(props);
        this.state={
           todos:[{title:"first Todo",complete:false},{title:"second Todo",complete:true}]
        }
    }
    
    render(){
    return(
    <div>
        <h1 className='heading1'>Todo Management System</h1>
        {this.state.todos.map(todo=>(
            <Todo/>
        ))}
    </div>
    )
}
}
//export default App
