import React,{Component} from 'react'
import './App.css'
import Todo from './components/Todo.js'
import TodoForm from './components/TodoForm';
export default class App extends Component
{
    constructor(props)
    {
        super(props);
        this.state={
           todos:[{id:1,title:"first Todo",complete:false},{id:2,title:"second Todo",complete:false}]
        }
    }
    addTodo=(title)=>{
      console.log(title);
      let todos=[...this.state.todos];
      let id=todos[todos.length-1]?todos[todos.length-1]['id']+1:0;
      let newTodo=
      {
              id,
              title,
              complete:false
      };
      todos.push(newTodo);
      this.setState({todos});
    }

  completeTodo=(id)=>{
  let todos=[...this.state.todos];
  todos.filter(todo=>{
     if(todo.id===id)
     {
         todo.complete=true;

     }
  });
  this.setState({todos})
  }
deleteTodo=(id)=>
{
  let todos=[...this.state.todos];
  todos.filter((todo,index)=>{
    if(todo.id===id)
    {
        todo.splice(index,1);

    }
})
  this.setState({todos});
};

    render(){
    return(
    <div>
        <h1 className='heading1'>Todo Management System</h1>
        {this.state.todos.map(todo=>(
            <Todo key={todo.id} todo={todo} completeTodo={(id)=>this.completeTodo(id)}
            deleteTodo={(id)=>this.deleteTodo(id)}
            />
        ))}
        <TodoForm addTodo={(todo)=>this.addTodo(todo)}/>
    </div>
    )
}
}
//export default App
